"use client";

import { useState, useEffect } from "react";
import dynamic from "next/dynamic";

// Dynamic import of the scene to ensure NO server-side rendering of Three.js
const AvatarScene = dynamic(() => import("./AvatarScene"), { 
  ssr: false,
  loading: () => <div className="w-full h-full flex items-center justify-center text-cyan-500 text-xs">INITIALIZING AVATAR...</div>
});

export default function FemaleAvatar() {
  const [expression, setExpression] = useState('neutral');
  const [isSpeaking, setIsSpeaking] = useState(false);

  // Voice Event Listener
  useEffect(() => {
    if (typeof window === 'undefined') return;

    const handleVoice = (e: any) => {
        if (e.detail?.target === 'avatar') {
             if (e.detail.action === 'speak') {
                 setIsSpeaking(true);
                 setExpression('talking');
                 setTimeout(() => {
                     setIsSpeaking(false);
                     setExpression('neutral');
                 }, 3000); 
             }
             if (e.detail.action === 'emotion') {
                 setExpression(e.detail.value);
             }
        }
    };

    window.addEventListener('voice-command', handleVoice as any);
    return () => window.removeEventListener('voice-command', handleVoice as any);
  }, []);

  return (
    <div className="w-full h-full relative pointer-events-auto">
      {/* Status HUD */}
      <div className="absolute top-2 left-2 z-10 flex flex-col gap-1">
         <div className="flex items-center gap-2">
             <div className={`w-2 h-2 rounded-full ${isSpeaking ? 'bg-yellow-400 animate-ping' : 'bg-cyan-500'}`} />
             <span className="text-[10px] text-cyan-400 font-mono tracking-widest uppercase">
                 AI: {expression}
             </span>
         </div>
      </div>

      <AvatarScene expression={expression} isSpeaking={isSpeaking} />
    </div>
  );
}

/**
 * Real Analytics Dashboard Component
 * Displays live data from MongoDB
 */

"use client";

import { useAnalytics } from "@/hooks/useAPI";
import {
  BarChart3,
  DollarSign,
  Eye,
  RefreshCw,
  ShoppingCart,
  TrendingUp,
  Users,
} from "lucide-react";
import { useCallback, useEffect, useState } from "react";

interface StatCardProps {
  title: string;
  value: string | number;
  change?: string;
  icon: React.ReactNode;
  trend?: "up" | "down";
}

function StatCard({ title, value, change, icon, trend }: StatCardProps) {
  return (
    <div className="card">
      <div className="flex items-center justify-between mb-4">
        <div className="flex-1">
          <p className="text-sm text-gray-400 mb-1">{title}</p>
          <h3 className="text-3xl font-bold text-white">{value}</h3>
        </div>
        <div className="w-12 h-12 bg-gold rounded-full flex items-center justify-center">
          {icon}
        </div>
      </div>
      {change && trend && (
        <div
          className={`flex items-center text-sm ${trend === "up" ? "text-green-400" : "text-red-400"}`}
        >
          <TrendingUp
            size={16}
            className={trend === "down" ? "rotate-180" : ""}
          />
          <span className="ml-1">{change} from last month</span>
        </div>
      )}
    </div>
  );
}

export default function RealAnalytics() {
  const [timeRange, setTimeRange] = useState<"day" | "week" | "month">("day");
  const [stats, setStats] = useState<any>(null);
  const { fetchAnalytics, loading, error } = useAnalytics();

  const loadAnalytics = useCallback(async () => {
    try {
      const data = await fetchAnalytics(timeRange);
      setStats(data.stats);
    } catch (err: unknown) {
      console.error("Analytics load error:", err);
    }
  }, [fetchAnalytics, timeRange]);

  useEffect(() => {
    loadAnalytics();

    // Auto-refresh every 30 seconds
    const interval = setInterval(loadAnalytics, 30000);
    return () => clearInterval(interval);
  }, [loadAnalytics]);

  if (loading && !stats) {
    return (
      <div className="card">
        <div className="flex items-center justify-center py-12">
          <RefreshCw className="animate-spin text-gold" size={32} />
          <span className="ml-3 text-white">Loading analytics...</span>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Time Range Selector */}
      <div className="flex items-center justify-between">
        <h2 className="text-2xl font-bold text-white flex items-center gap-2">
          <BarChart3 className="text-gold" size={28} />
          Real-Time Analytics
        </h2>
        <div className="flex gap-2">
          {(["day", "week", "month"] as const).map((range) => (
            <button
              key={range}
              onClick={() => setTimeRange(range)}
              className={`px-4 py-2 rounded-lg font-medium transition-all ${
                timeRange === range
                  ? "bg-gold text-black"
                  : "glass border border-gray-700 text-gray-300 hover:border-gold"
              }`}
            >
              {range.charAt(0).toUpperCase() + range.slice(1)}
            </button>
          ))}
          <button
            onClick={loadAnalytics}
            disabled={loading}
            className="p-2 glass border border-gray-700 hover:border-gold rounded-lg transition-all"
            title="Refresh"
          >
            <RefreshCw
              className={`text-gold ${loading ? "animate-spin" : ""}`}
              size={20}
            />
          </button>
        </div>
      </div>

      {error && (
        <div className="p-4 bg-yellow-500/10 border border-yellow-500 rounded-lg text-yellow-400">
          ‚ö†Ô∏è {error} - Showing fallback data
        </div>
      )}

      {/* Stats Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <StatCard
          title="Total Revenue"
          value={`$${stats?.totalRevenue?.toLocaleString() || "0"}`}
          change="+23.5%"
          icon={<DollarSign className="text-black" size={24} />}
          trend="up"
        />
        <StatCard
          title="Active Users"
          value={stats?.activeUsers?.toLocaleString() || "0"}
          change="+12.3%"
          icon={<Users className="text-black" size={24} />}
          trend="up"
        />
        <StatCard
          title="Store Orders"
          value={stats?.storeOrders?.toLocaleString() || "0"}
          change="+8.1%"
          icon={<ShoppingCart className="text-black" size={24} />}
          trend="up"
        />
        <StatCard
          title="Live Viewers"
          value={stats?.liveViewers?.toLocaleString() || "0"}
          change="-5.2%"
          icon={<Eye className="text-black" size={24} />}
          trend="down"
        />
      </div>

      {/* Additional Metrics */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        <div className="card">
          <h3 className="text-lg font-bold text-white mb-4">Page Views</h3>
          <div className="text-center">
            <p className="text-4xl font-bold gradient-text">
              {stats?.pageViews?.toLocaleString() || "0"}
            </p>
            <p className="text-sm text-gray-400 mt-2">
              Total page views this {timeRange}
            </p>
          </div>
        </div>

        <div className="card">
          <h3 className="text-lg font-bold text-white mb-4">Conversion Rate</h3>
          <div className="text-center">
            <p className="text-4xl font-bold gradient-text">
              {stats?.conversionRate?.toFixed(2) || "0"}%
            </p>
            <p className="text-sm text-gray-400 mt-2">
              Visitor to customer conversion
            </p>
          </div>
        </div>
      </div>

      <div className="text-xs text-gray-500 text-center">
        üìä Connected to MongoDB ‚Ä¢ Auto-refreshes every 30 seconds
      </div>
    </div>
  );
}

/**
 * Stream Control Component
 * WebRTC broadcast controls for THE MATRIX
 */

'use client';

import { useState, useRef, useEffect } from 'react';
import { Video, VideoOff, Radio, Users, MessageCircle } from 'lucide-react';
import { useStreaming } from '@/hooks/useAPI';
import { WebRTCBroadcaster } from '@/lib/services/webrtc';

export default function StreamControl() {
  const [isStreaming, setIsStreaming] = useState(false);
  const [streamId, setStreamId] = useState<string | null>(null);
  const [viewerCount, setViewerCount] = useState(0);
  const [streamTitle, setStreamTitle] = useState('');
  const videoRef = useRef<HTMLVideoElement>(null);
  const broadcasterRef = useRef<WebRTCBroadcaster | null>(null);

  const { startStream, stopStream, getStreamStatus, loading, error } = useStreaming();

  useEffect(() => {
    // Poll for viewer count when streaming
    if (isStreaming && streamId) {
      const interval = setInterval(async () => {
        try {
          const status = await getStreamStatus(streamId);
          setViewerCount(status.viewerCount || 0);
        } catch (err: unknown) {
          console.error('Status check error:', err);
        }
      }, 5000);

      return () => clearInterval(interval);
    }
  }, [isStreaming, streamId]);

  const handleStartStream = async () => {
    if (!streamTitle.trim()) {
      alert('Please enter a stream title');
      return;
    }

    try {
      // Initialize stream on server
      const streamData = await startStream(streamTitle);
      setStreamId(streamData.streamId);

      // Initialize WebRTC broadcaster
      const broadcaster = new WebRTCBroadcaster();
      broadcasterRef.current = broadcaster;

      // Start local media
      const localStream = await broadcaster.startBroadcast();

      if (videoRef.current) {
        videoRef.current.srcObject = localStream;
      }

      // Create offer and send to signaling server
      const offer = await broadcaster.createOffer();
      console.log('WebRTC Offer created:', offer);

      // In production, send offer to signaling server
      // await fetch(streamData.signalServerUrl, { ... })

      setIsStreaming(true);
    } catch (err: unknown) {
      console.error('Start stream error:', err);
      alert('Failed to start stream. Please check camera/microphone permissions.');
    }
  };

  const handleStopStream = async () => {
    if (streamId) {
      try {
        await stopStream(streamId);
      } catch (err: unknown) {
        console.error('Stop stream error:', err);
      }
    }

    if (broadcasterRef.current) {
      broadcasterRef.current.stopBroadcast();
      broadcasterRef.current = null;
    }

    setIsStreaming(false);
    setStreamId(null);
    setViewerCount(0);
  };

  return (
    <div className="card bg-gradient-to-r from-purple-900/20 to-pink-900/20 border-purple-500">
      <h3 className="text-xl font-bold text-white mb-4 flex items-center gap-2">
        <Video className="text-purple-400" size={24} />
        Live Stream Control
      </h3>

      {/* Stream Preview */}
      <div className="mb-4 bg-black rounded-lg overflow-hidden aspect-video relative">
        <video
          ref={videoRef}
          autoPlay
          muted
          playsInline
          className="w-full h-full object-cover"
        />

        {isStreaming && (
          <div className="absolute top-4 left-4 right-4 flex justify-between items-start">
            <div className="px-3 py-2 bg-red-600 rounded-lg flex items-center gap-2">
              <Radio className="text-white animate-pulse" size={16} />
              <span className="text-white text-sm font-semibold">LIVE</span>
            </div>
            <div className="px-3 py-2 bg-black/70 rounded-lg flex items-center gap-2">
              <Users className="text-white" size={16} />
              <span className="text-white text-sm font-semibold">{viewerCount}</span>
            </div>
          </div>
        )}

        {!isStreaming && (
          <div className="absolute inset-0 flex items-center justify-center bg-gray-900">
            <div className="text-center">
              <VideoOff className="text-gray-600 mx-auto mb-2" size={48} />
              <p className="text-gray-400">Stream Offline</p>
            </div>
          </div>
        )}
      </div>

      {/* Controls */}
      {!isStreaming ? (
        <div className="space-y-3">
          <input
            type="text"
            value={streamTitle}
            onChange={(e) => setStreamTitle(e.target.value)}
            placeholder="Enter stream title..."
            className="w-full px-4 py-3 bg-gray-900 border border-gray-700 rounded-lg text-white placeholder-gray-500 focus:outline-none focus:ring-2 focus:ring-purple-500"
          />
          <button
            onClick={handleStartStream}
            disabled={loading || !streamTitle.trim()}
            className="w-full py-3 bg-purple-600 hover:bg-purple-700 text-white rounded-lg font-semibold transition-all flex items-center justify-center gap-2 disabled:opacity-50"
          >
            <Video size={20} />
            Start Broadcast
          </button>
        </div>
      ) : (
        <div className="space-y-3">
          <div className="p-4 bg-gray-900 rounded-lg border border-gray-700">
            <p className="text-sm font-semibold text-purple-400 mb-1">Now Streaming:</p>
            <p className="text-white">{streamTitle}</p>
            <p className="text-xs text-gray-500 mt-2">Stream ID: {streamId}</p>
          </div>
          <button
            onClick={handleStopStream}
            disabled={loading}
            className="w-full py-3 bg-red-600 hover:bg-red-700 text-white rounded-lg font-semibold transition-all flex items-center justify-center gap-2"
          >
            <VideoOff size={20} />
            Stop Broadcast
          </button>
        </div>
      )}

      {error && (
        <div className="mt-4 p-3 bg-red-500/10 border border-red-500 rounded-lg text-red-400 text-sm">
          {error}
        </div>
      )}

      {/* Stream Info */}
      <div className="mt-4 p-4 bg-gray-900/50 rounded-lg border border-gray-700">
        <p className="text-xs text-gray-400">
          üí° Viewers can watch at: <span className="text-purple-400">/live</span>
        </p>
        <p className="text-xs text-gray-500 mt-1">
          WebRTC streaming with TURN server support
        </p>
      </div>
    </div>
  );
}

'use client';

import { AlertCircle, CheckCircle, Eye, Mic, StopCircle } from 'lucide-react';
import { useEffect, useRef, useState } from 'react';

interface CodePatch {
  file: string;
  description: string;
  oldCode: string;
  newCode: string;
}

interface VoiceResponse {
  success: boolean;
  intent: string;
  description: string;
  patches: CodePatch[];
  preview: string;
  timestamp: string;
}

export default function VoiceCodeEditor() {
  const [isListening, setIsListening] = useState(false);
  const [transcript, setTranscript] = useState('');
  const [isProcessing, setIsProcessing] = useState(false);
  const [response, setResponse] = useState<VoiceResponse | null>(null);
  const [showPreview, setShowPreview] = useState(false);
  const [error, setError] = useState('');
  const [status, setStatus] = useState('');
  const recognitionRef = useRef<any>(null);

  // Cleanup on unmount
  useEffect(() => {
    return () => {
      if (recognitionRef.current) {
        try {
          recognitionRef.current.stop();
        } catch {}
        recognitionRef.current = null;
      }
    };
  }, []);

  // Browser Web Speech API for voice recognition
  const startListening = async () => {
    // Prevent double-listen: if already listening, do nothing
    if (isListening || recognitionRef.current) {
      return;
    }

    try {
      setError('');
      setStatus('üé§ Listening...');

      const SpeechRecognition = (window as any).webkitSpeechRecognition || (window as any).SpeechRecognition;
      if (!SpeechRecognition) {
        setError('Speech Recognition not supported in your browser');
        return;
      }

      const recognition = new SpeechRecognition();
      recognitionRef.current = recognition;
      recognition.continuous = false; // Changed to false to prevent repetition
      recognition.interimResults = false; // Only final results
      recognition.language = 'en-US';

      recognition.onstart = () => {
        setIsListening(true);
        setTranscript('');
        setStatus('üé§ Listening... Speak your command and it will process automatically.');
      };

      recognition.onresult = (event: any) => {
        const transcript_part = event.results[0][0].transcript;
        if (event.results[0].isFinal) {
          setTranscript(transcript_part);
        }
      };

      recognition.onerror = (event: any) => {
        console.error('[VOICE] Recognition error:', event.error);
        setError(`Error: ${event.error}`);
        setIsListening(false);
        recognitionRef.current = null;

        // Auto-disable on repeated errors (failsafe)
        if (event.error === 'no-speech' || event.error === 'aborted') {
          setStatus('‚ö†Ô∏è Voice disabled - click "Start Speaking" to retry');
        }
      };

      recognition.onend = () => {
        setIsListening(false);
        setStatus('');
        recognitionRef.current = null;
      };

      recognition.start();
    } catch (err: unknown) {
      setError(`Listening failed: ${err}`);
      setIsListening(false);
      recognitionRef.current = null;
    }
  };

  const stopListening = () => {
    if (recognitionRef.current) {
      try {
        recognitionRef.current.stop();
      } catch (err: unknown) {
        console.error('Stop recognition error:', err);
      }
      recognitionRef.current = null;
    }
    setIsListening(false);
    setStatus('');
  };

  const processCommand = async () => {
    if (!transcript.trim()) {
      setError('Please speak a command first');
      return;
    }

    setIsProcessing(true);
    setError('');
    setStatus('ü§ñ Processing your command...');

    try {
      const res = await fetch('/api/voice-to-code', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          transcript: transcript.trim(),
          action: 'preview',
        }),
      });

      if (!res.ok) {
        const errorData = await res.json();
        setError(errorData.error || 'Failed to process command');
        return;
      }

      const data = (await res.json()) as VoiceResponse;
      setResponse(data);
      setShowPreview(true);
      setStatus(`‚úÖ Ready to preview: "${data.intent}"`);
    } catch (err: unknown) {
      setError(`Processing failed: ${err}`);
    } finally {
      setIsProcessing(false);
    }
  };

  const commitChanges = async () => {
    if (!response) return;

    setIsProcessing(true);
    setStatus('üì§ Committing changes...');

    try {
      // Call API to commit the changes
      const res = await fetch('/api/voice-to-code', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          transcript: transcript.trim(),
          action: 'commit',
        }),
      });

      if (!res.ok) {
        const errorData = await res.json();
        setError(errorData.error || 'Failed to commit changes');
        return;
      }

      setStatus('‚úÖ Changes committed! Ready to deploy.');
      setTimeout(() => {
        setTranscript('');
        setResponse(null);
        setShowPreview(false);
        setStatus('');
      }, 2000);
    } catch (err: unknown) {
      setError(`Commit failed: ${err}`);
    } finally {
      setIsProcessing(false);
    }
  };

  const resetForm = () => {
    setTranscript('');
    setResponse(null);
    setShowPreview(false);
    setError('');
    setStatus('');
  };

  return (
    <div className="space-y-6">
      {/* Voice Input Section */}
      <div className="card">
        <div className="flex items-center justify-between mb-4">
          <h3 className="text-xl font-bold text-gold">üé§ Voice Command</h3>
          {isListening && <span className="animate-pulse text-red-500">Recording...</span>}
        </div>

        {/* Transcript Display */}
        {transcript && (
          <div className="bg-gray-900 border border-gold/20 rounded-lg p-4 mb-4">
            <p className="text-sm text-gray-400 mb-2">Your command:</p>
            <p className="text-white text-lg">{transcript}</p>
          </div>
        )}

        {/* Mic Button */}
        <div className="flex gap-3 mb-4">
          {!isListening ? (
            <button
              onClick={startListening}
              disabled={isProcessing}
              className="flex items-center gap-2 btn-primary px-6 py-3 disabled:opacity-50"
            >
              <Mic size={20} />
              Start Speaking
            </button>
          ) : (
            <button
              onClick={stopListening}
              className="flex items-center gap-2 bg-red-600 hover:bg-red-700 text-white px-6 py-3 rounded-lg font-semibold"
            >
              <StopCircle size={20} />
              Stop
            </button>
          )}

          {transcript && !isListening && (
            <button
              onClick={processCommand}
              disabled={isProcessing}
              className="flex items-center gap-2 bg-sapphire hover:bg-sapphire/80 text-white px-6 py-3 rounded-lg font-semibold disabled:opacity-50"
            >
              <Eye size={20} />
              {isProcessing ? 'Processing...' : 'Preview Changes'}
            </button>
          )}

          {(transcript || response) && (
            <button
              onClick={resetForm}
              className="px-4 py-3 bg-gray-700 hover:bg-gray-600 text-white rounded-lg"
            >
              Clear
            </button>
          )}
        </div>

        {/* Status Message */}
        {status && (
          <div className="p-3 bg-sapphire/10 border border-sapphire/50 rounded-lg text-sapphire text-sm">
            {status}
          </div>
        )}

        {/* Error Message */}
        {error && (
          <div className="p-3 bg-red-900/30 border border-red-500/50 rounded-lg text-red-200 text-sm flex items-center gap-2">
            <AlertCircle size={18} />
            {error}
          </div>
        )}
      </div>

      {/* Preview Section */}
      {showPreview && response && (
        <div className="card border-2 border-gold">
          <div className="mb-6">
            <h3 className="text-2xl font-bold text-gold mb-2">{response.intent}</h3>
            <p className="text-gray-300">{response.description}</p>
          </div>

          {/* Code Diff Preview */}
          <div className="bg-black/50 border border-gold/20 rounded-lg p-6 mb-6 max-h-96 overflow-y-auto">
            <div dangerouslySetInnerHTML={{ __html: response.preview }} />
          </div>

          {/* Commit/Deploy Buttons */}
          <div className="flex gap-3 flex-wrap">
            <button
              onClick={commitChanges}
              disabled={isProcessing}
              className="flex items-center gap-2 btn-primary px-8 py-3 font-bold disabled:opacity-50"
            >
              <CheckCircle size={20} />
              {isProcessing ? 'Committing...' : 'Commit & Deploy'}
            </button>
            <button
              onClick={() => setShowPreview(false)}
              className="px-8 py-3 bg-gray-700 hover:bg-gray-600 text-white rounded-lg font-semibold"
            >
              Cancel
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

'use client';

import { motion } from 'framer-motion';
import { BarChart3, Cpu, Globe, Shield, Target, Zap } from 'lucide-react';
import Card from '../../ui/Card';

export default function GlobalDashboard() {
  return (
    <div className="container-standard py-12">
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        className="space-y-12"
      >
        <div className="text-center">
          <h1 className="text-5xl font-black text-white tracking-tighter uppercase mb-2">
            Nexus Performance
          </h1>
          <p className="text-[#D4AF37] text-[10px] font-black uppercase tracking-[0.4em]">
            Real-time Global Infrastructure Audit
          </p>
        </div>

        {/* Global Stats Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
          <StatCard icon={Globe} label="Network Reach" value="GLOBAL" sub="142 Nodes Active" />
          <StatCard icon={Zap} label="Response Latency" value="1.2ms" sub="Optimization Peak" />
          <StatCard icon={Shield} label="Security Index" value="100.0" sub="Zero Breach State" />
          <StatCard icon={Cpu} label="Compute Load" value="14%" sub="Scaled for Demand" />
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          {/* Main Visual: Performance Map Placeholder */}
          <Card className="lg:col-span-2 bg-white/5 border-white/10 p-12 h-[500px] flex flex-col items-center justify-center relative shadow-2xl">
            <div className="absolute inset-0 bg-linear-to-br from-[#D4AF37]/5 to-transparent pointer-events-none" />
            <BarChart3 className="text-[#D4AF37]/20 mb-6" size={80} />
            <h2 className="text-white/40 text-[10px] font-black uppercase tracking-widest mb-2 italic">
              Neural Throughput Visualization
            </h2>
            <div className="w-full max-w-md h-1 bg-white/10 rounded-full overflow-hidden">
              <motion.div
