            throw new Error(`News API responded with ${response.status}`);
        }

        const data = await response.json();

        // Transform to our app's format
        const news = data.news.map((item: any, index: number) => ({
            id: item.id || index,
            title: item.title,
            category: 'GLOBAL', // World News API might not give categories easily in search-news
            image: item.image || 'https://images.unsplash.com/photo-1504711434969-e33886168f5c?w=200&q=80',
            timestamp: new Date(item.publish_date).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })
        }));

        return NextResponse.json(news);
    } catch (error: unknown) {
        console.error('News API Error:', error);
        return NextResponse.json({ error: 'Failed to fetch news' }, { status: 500 });
    }
}

/**
 * PayPal Capture Order API Route
 * Captures approved PayPal orders
 */

import { NextRequest, NextResponse } from 'next/server';
import { captureOrder, trackAffiliateSale } from '@/lib/services/paypal';
import { getOrders } from '@/lib/services/mongodb';

export async function POST(request: NextRequest) {
  try {
    const body = await request.json();
    const { orderId } = body;

    if (!orderId) {
      return NextResponse.json(
        { error: 'Order ID required' },
        { status: 400 }
      );
    }

    // Capture the PayPal order
    const captureResult = await captureOrder(orderId);

    // Get order details from database to find affiliate products
    const orders = await getOrders(1);
    const order = orders.find(o => o.orderId === orderId);

    // Track affiliate sales if applicable
    if (order) {
      const affiliateProducts = order.items
        .filter((item: any) => item.affiliateLink)
        .map((item: any) => ({
          productId: item.productId,
          affiliateLink: item.affiliateLink,
          commission: item.commission || 0,
        }));

      if (affiliateProducts.length > 0) {
        await trackAffiliateSale(orderId, affiliateProducts);
      }
    }

    return NextResponse.json({
      success: true,
      captureId: captureResult.id,
      status: captureResult.status,
      payer: captureResult.payer,
    });
  } catch (error: unknown) {
    console.error('PayPal capture order error:', error);
    return NextResponse.json(
      { error: 'Failed to capture PayPal order' },
      { status: 500 }
    );
  }
}

/**
 * PayPal Checkout API Route
 * Creates PayPal orders and captures payments
 */

import { RATE_LIMITS, withRateLimit } from '@/lib/rate-limit';
import { withSecurity } from '@/lib/security';
import { NextRequest, NextResponse } from 'next/server';

export const runtime = 'nodejs';

// PayPal SDK will be used server-side
const PAYPAL_CLIENT_ID = process.env.PAYPAL_CLIENT_ID;
const PAYPAL_CLIENT_SECRET = process.env.PAYPAL_CLIENT_SECRET;
const PAYPAL_API_BASE = process.env.NODE_ENV === 'production'
  ? 'https://api-m.paypal.com'
  : 'https://api-m.sandbox.paypal.com';

async function getPayPalAccessToken(): Promise<string> {
  if (!PAYPAL_CLIENT_ID || !PAYPAL_CLIENT_SECRET) {
    throw new Error('PayPal credentials not configured');
  }

  const auth = Buffer.from(`${PAYPAL_CLIENT_ID}:${PAYPAL_CLIENT_SECRET}`).toString('base64');

  const response = await fetch(`${PAYPAL_API_BASE}/v1/oauth2/token`, {
    method: 'POST',
    headers: {
      'Authorization': `Basic ${auth}`,
      'Content-Type': 'application/x-www-form-urlencoded',
    },
    body: 'grant_type=client_credentials',
  });

  const data = await response.json();
  return data.access_token;
}

const handler = async (request: NextRequest) => {
  try {
    const body = await request.json();
    const { action, orderID, amount, currency = 'USD', items } = body;

    if (action === 'create') {
      // Create PayPal order
      const accessToken = await getPayPalAccessToken();

      const orderData = {
        intent: 'CAPTURE',
        purchase_units: [{
          amount: {
            currency_code: currency,
            value: amount.toFixed(2),
            breakdown: {
              item_total: {
                currency_code: currency,
                value: amount.toFixed(2)
              }
            }
          },
          items: items?.map((item: any) => ({
            name: item.name,
            quantity: item.quantity.toString(),
            unit_amount: {
              currency_code: currency,
              value: item.price.toFixed(2)
            }
          }))
        }],
        application_context: {
          brand_name: '3000 Studios',
          shipping_preference: 'NO_SHIPPING',
          user_action: 'PAY_NOW',
          return_url: `${process.env.NEXT_PUBLIC_SITE_URL}/store/success`,
          cancel_url: `${process.env.NEXT_PUBLIC_SITE_URL}/store/checkout`
        }
      };

      const response = await fetch(`${PAYPAL_API_BASE}/v2/checkout/orders`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${accessToken}`,
        },
        body: JSON.stringify(orderData),
      });

      const order = await response.json();

      if (!response.ok) {
        console.error('PayPal create order error:', order);
        return NextResponse.json(
          { error: 'Failed to create PayPal order', details: order },
          { status: 400 }
        );
      }

      return NextResponse.json({ orderID: order.id });
    }

    if (action === 'capture') {
      // Capture PayPal payment
      const accessToken = await getPayPalAccessToken();

      const response = await fetch(
        `${PAYPAL_API_BASE}/v2/checkout/orders/${orderID}/capture`,
        {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${accessToken}`,
          },
        }
      );

      const captureData = await response.json();

      if (!response.ok) {
        console.error('PayPal capture error:', captureData);
        return NextResponse.json(
          { error: 'Failed to capture payment', details: captureData },
          { status: 400 }
        );
      }

      // Log successful payment
      // Payment captured successfully

      return NextResponse.json({
        success: true,
        orderID,
        status: captureData.status,
        details: captureData
      });
    }

    return NextResponse.json(
      { error: 'Invalid action' },
      { status: 400 }
    );
  } catch (error: unknown) {
    console.error('PayPal API error:', error);
    return NextResponse.json(
      { error: 'Payment processing failed', message: error instanceof Error ? (error instanceof Error ? (error instanceof Error ? error.message : "Unknown error") : "Unknown error") : 'Unknown error' },
      { status: 500 }
    );
  }
};

export const POST = withRateLimit(
  withSecurity(handler, { csrf: false, cors: true }),
  RATE_LIMITS.payment
);

/**
 * PayPal Create Order API Route
 * Handles PayPal payment processing
 */

import { NextRequest, NextResponse } from 'next/server';
import { createOrder, trackAffiliateSale } from '@/lib/services/paypal';
// @ts-nocheck

export async function POST(request: NextRequest) {
  try {
    const body = await request.json();
    const { items, userId } = body;

    if (!items || items.length === 0) {
      return NextResponse.json({ error: 'No items in cart' }, { status: 400 });
    }

    // Calculate total
    const total = items.reduce((sum: number, item: any) => {
      return sum + item.price * item.quantity;
    }, 0);

    // Create PayPal order
    const paypalOrder = await createOrder({
      items: items.map((item: any) => ({
        name: item.name,
        description: item.description || item.name,
        quantity: item.quantity,
        unit_amount: {
          currency_code: 'USD',
          value: item.price.toFixed(2),
        },
        affiliate_link: item.affiliateLink,
      })),
      total: total.toFixed(2),
      currency: 'USD',
    });

    return NextResponse.json({
      success: true,
      orderId: paypalOrder.id,
      approvalUrl: paypalOrder.links.find((link: any) => link.rel === 'approve')?.href,
    });
  } catch (error: unknown) {
    console.error('PayPal create order error:', error);
    return NextResponse.json({ error: 'Failed to create PayPal order' }, { status: 500 });
  }
}

