'use client';

import { AlertCircle, Check, Gift, Loader2, Mail } from 'lucide-react';
import { useState } from 'react';

interface NewsletterProps {
  variant?: 'default' | 'compact' | 'hero';
  title?: string;
  description?: string;
  showBenefits?: boolean;
  className?: string;
}

export default function Newsletter({
  variant = 'default',
  title = 'Join Our Newsletter',
  description = 'Get exclusive insights, industry trends, and special offers delivered to your inbox',
  showBenefits = true,
  className = '',
}: NewsletterProps) {
  const [email, setEmail] = useState('');
  const [name, setName] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [status, setStatus] = useState<'idle' | 'success' | 'error'>('idle');
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');

  const validateEmail = (email: string): boolean => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    // Validation
    if (!email.trim()) {
      setError('Email is required');
      return;
    }

    if (!validateEmail(email)) {
      setError('Please enter a valid email address');
      return;
    }

    setIsSubmitting(true);
    setError('');
    setStatus('idle');

    try {
      // TODO: Replace with actual API endpoint
      await new Promise((resolve) => setTimeout(resolve, 1500));
      setStatus('success');
      setMessage("You're subscribed! Check your inbox for updates.");
    } catch {
      setStatus('error');
      setMessage('Something went wrong. Please try again later.');
    } finally {
      setIsSubmitting(false);
    }
  };

  const benefits = [
    'Weekly industry insights',
    'Exclusive tips & tutorials',
    'Early access to new products',
    'Special subscriber-only discounts',
  ];

  if (variant === 'compact') {
    return (
      <div className={`${className}`}>
        <form onSubmit={handleSubmit} className="flex flex-col sm:flex-row gap-3">
