/**
 * Shadow AI Avatar Component (Home Page Version)
 * This is the conversational, animated avatar that greets visitors
 * Features:
 * - Speech recognition and response
 * - Physics-based animations
 * - Gyroscope responsive (on mobile)
 * - Does NOT edit the website (that's only in THE MATRIX)
 * - Fun, conversational personality
 *
 * NOTE: Full 3D implementation requires Three.js/R3F
 * This is a foundational structure that can be enhanced with 3D models
 */

'use client';

import { useCallback, useEffect, useRef, useState } from 'react';
import { Mic, MicOff, Volume2, VolumeX } from 'lucide-react';

export default function ShadowAvatar() {
  const [isListening, setIsListening] = useState(false);
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [userText, setUserText] = useState('');
  const [avatarText, setAvatarText] = useState("Hey there! I'm Shadow. Ask me anything!");
  const [audioEnabled, setAudioEnabled] = useState(true);
  const recognitionRef = useRef<any>(null);

  const handleUserInput = useCallback(
    async (input: string) => {
      // Simulate AI response (in production, this would call an AI API)
      const responses = [
        "That's interesting! Want to check out our store?",
        'I like the way you think! Have you seen our projects?',
        "Great question! I'm here to help you navigate 3000 Studios.",
        'Fascinating! You should definitely explore our portfolio.',
        "I hear you! Let me know if you want to see what we're working on.",
        'Cool! Check out our live streams for more content.',
      ];

      const randomResponse = responses[Math.floor(Math.random() * responses.length)];
      setAvatarText(randomResponse);

      if (audioEnabled && 'speechSynthesis' in window) {
        const utterance = new SpeechSynthesisUtterance(randomResponse);
        utterance.rate = 1.1;
        utterance.pitch = 1.0;
        utterance.onstart = () => setIsSpeaking(true);
        utterance.onend = () => setIsSpeaking(false);
        window.speechSynthesis.speak(utterance);
      }
    },
    [audioEnabled]
  );

  // Initialize speech recognition
  useEffect(() => {
    if (typeof window !== 'undefined') {
      const SpeechRecognition =
        (window as any).SpeechRecognition || (window as any).webkitSpeechRecognition;

      if (!SpeechRecognition) {
        console.warn('Speech recognition not supported in this browser');
        setAvatarText(
          "Hey! Speech recognition isn't supported in your browser, but I can still chat via text!"
        );
        return;
      }

      recognitionRef.current = new SpeechRecognition();
      recognitionRef.current.continuous = false;
      recognitionRef.current.interimResults = false;
      recognitionRef.current.lang = 'en-US';

      recognitionRef.current.onresult = (event: any) => {
        const transcript = event.results[0][0].transcript;
        setUserText(transcript);
        handleUserInput(transcript);
      };

      recognitionRef.current.onerror = (event: any) => {
        console.error('Speech recognition error:', event.error);
        setIsListening(false);
      };

      recognitionRef.current.onend = () => {
        setIsListening(false);
      };
    }
  }, [handleUserInput]);

  const toggleListening = () => {
    if (!recognitionRef.current) {
      alert('Speech recognition is not supported in your browser. Please use Chrome or Edge.');
      return;
    }

    if (isListening) {
      recognitionRef.current.stop();
      setIsListening(false);
    } else {
      recognitionRef.current.start();
      setIsListening(true);
    }
  };

  return (
    <div className="fixed bottom-8 right-8 z-50 max-w-sm">
      {/* Avatar Container */}
      <div className="bg-black/80 backdrop-blur-xl border-2 border-gold rounded-2xl p-6 shadow-2xl">
        {/* Avatar Visual (Placeholder for 3D model) */}
        <div className="relative mb-4">
          <div className="w-32 h-32 mx-auto bg-gradient-to-br from-gold via-sapphire to-platinum rounded-full flex items-center justify-center animate-pulse">
            <div
              className={`w-24 h-24 bg-black rounded-full flex items-center justify-center ${isSpeaking ? 'animate-ping' : ''}`}
            >
              <span className="text-4xl">üë§</span>
            </div>
          </div>
          {isSpeaking && (
            <div className="absolute -bottom-2 left-1/2 transform -translate-x-1/2">
              <div className="flex gap-1">
                {[...Array(5)].map((_, i) => (
                  <div
                    key={i}
                    className="w-1 bg-gold rounded-full animate-pulse"
                    style={{
                      height: Math.random() * 20 + 10 + 'px',
                      animationDelay: `${i * 0.1}s`,
                    }}
                  />
                ))}
              </div>
            </div>
          )}
        </div>

        {/* Avatar Name */}
        <h3 className="text-center text-gold font-bold text-xl mb-2">Shadow AI</h3>

        {/* Avatar Speech Bubble */}
        <div className="bg-gray-900 rounded-lg p-4 mb-4 min-h-[80px] border border-gold/30">
          <p className="text-white text-sm italic">"{avatarText}"</p>
        </div>

        {/* User Input Display */}
        {userText && (
          <div className="bg-sapphire/20 rounded-lg p-3 mb-4 border border-sapphire/30">
            <p className="text-gray-300 text-xs">You: "{userText}"</p>
          </div>
        )}

        {/* Controls */}
        <div className="flex justify-center gap-4">
          <button
            onClick={toggleListening}
            className={`p-3 rounded-full transition-all ${
              isListening
                ? 'bg-red-500 hover:bg-red-600 animate-pulse'
                : 'bg-gold hover:bg-platinum'
            }`}
            title={isListening ? 'Stop listening' : 'Start listening'}
          >
            {isListening ? (
              <MicOff className="text-white" size={20} />
            ) : (
              <Mic className="text-black" size={20} />
            )}
          </button>

          <button
            onClick={() => setAudioEnabled(!audioEnabled)}
            className={`p-3 rounded-full transition-all ${
              audioEnabled ? 'bg-sapphire hover:bg-blue-600' : 'bg-gray-600 hover:bg-gray-700'
            }`}
            title={audioEnabled ? 'Mute audio' : 'Enable audio'}
          >
            {audioEnabled ? (
              <Volume2 className="text-white" size={20} />
            ) : (
              <VolumeX className="text-white" size={20} />
            )}
          </button>
        </div>

        {/* Info Note */}
        <p className="text-xs text-gray-500 text-center mt-4">
          I'm here to chat and help! (I don't edit the site - that's in THE MATRIX)
        </p>
      </div>
    </div>
  );
}

"use client";

import { useEffect, useRef, useState } from "react";

/**
 * Interactive Sound Effects System
 * Plays sophisticated click sounds and ambient audio
 * Includes "Enable Sound" overlay for browser autoplay policies
 */
export default function SoundEffects() {
  const audioContextRef = useRef<AudioContext | null>(null);
  const [enabled, setEnabled] = useState(false);
  const [showOverlay, setShowOverlay] = useState(false);

  useEffect(() => {
    // Initialize Web Audio API
    if (typeof window === "undefined") return;

    const AudioContext =
      window.AudioContext || (window as any).webkitAudioContext;
    audioContextRef.current = new AudioContext();
    audioContextRef.current.resume();

    setEnabled(true);
    setShowOverlay(false);
    localStorage.setItem("sound-enabled", "true");
  }, []);

  const disableSound = () => {
    setEnabled(false);
    setShowOverlay(false);
    localStorage.setItem('sound-enabled', 'false');
  };

  const enableSound = () => {
    setEnabled(true);
    setShowOverlay(false);
    localStorage.setItem("sound-enabled", "true");
  };

  useEffect(() => {
    if (!enabled) return;

    // Initialize Web Audio API if not already
    if (!audioContextRef.current) {
      const AudioContext = window.AudioContext || (window as any).webkitAudioContext;
      audioContextRef.current = new AudioContext();
    }

    // Function to create a sophisticated click sound
    const playClickSound = () => {
      if (!audioContextRef.current || audioContextRef.current.state === 'suspended') return;

      const context = audioContextRef.current;
      const oscillator = context.createOscillator();
      const gainNode = context.createGain();

      oscillator.connect(gainNode);
      gainNode.connect(context.destination);

      // Sophisticated tone - higher frequency for elegant sound
      oscillator.frequency.value = 800;
      oscillator.type = "sine";

      // Envelope for natural sound
      const now = context.currentTime;
      gainNode.gain.setValueAtTime(0, now);
      gainNode.gain.linearRampToValueAtTime(0.15, now + 0.01);
      gainNode.gain.exponentialRampToValueAtTime(0.01, now + 0.1);

      oscillator.start(now);
      oscillator.stop(now + 0.1);
    };

    // Function to create a hover sound
    const playHoverSound = () => {
      if (!audioContextRef.current || audioContextRef.current.state === 'suspended') return;

      const context = audioContextRef.current;
      const oscillator = context.createOscillator();
      const gainNode = context.createGain();

      oscillator.connect(gainNode);
      gainNode.connect(context.destination);

      oscillator.frequency.value = 600;
      oscillator.type = "sine";

      const now = context.currentTime;
      gainNode.gain.setValueAtTime(0, now);
      gainNode.gain.linearRampToValueAtTime(0.08, now + 0.01);
      gainNode.gain.exponentialRampToValueAtTime(0.01, now + 0.08);

      oscillator.start(now);
      oscillator.stop(now + 0.08);
    };

    // Add click sound to all interactive elements
    const addSoundToElements = () => {
      const buttons = document.querySelectorAll('button, a, [role="button"]');

      buttons.forEach((element) => {
        element.addEventListener("click", playClickSound);
        element.addEventListener("mouseenter", playHoverSound);
      });
    };

    // Initial setup
    addSoundToElements();

    // Observer to add sounds to dynamically added elements
    const observer = new MutationObserver(() => {
      addSoundToElements();
    });

    observer.observe(document.body, {
      childList: true,
      subtree: true,
    });

    return () => {
      observer.disconnect();
      if (audioContextRef.current) {
        audioContextRef.current.close();
      }
    };
  }, [enabled]);

  if (!showOverlay) return null;

  return (
    <div className="fixed bottom-4 right-4 z-50 bg-black/80 border border-yellow-500/30 p-4 rounded-lg shadow-lg backdrop-blur-md flex flex-col gap-2 max-w-xs animate-in fade-in slide-in-from-bottom-4">
      <p className="text-sm text-white font-medium">Enable immersive audio effects?</p>
      <div className="flex gap-2">
        <button 
          onClick={enableSound}
          className="flex-1 bg-yellow-500/20 hover:bg-yellow-500/40 text-yellow-500 border border-yellow-500/50 rounded px-3 py-1 text-xs transition-colors font-bold"
        >
          Enable
        </button>
        <button 
          onClick={disableSound}
          className="flex-1 bg-white/5 hover:bg-white/10 text-gray-400 border border-white/10 rounded px-3 py-1 text-xs transition-colors"
        >
          Dismiss
        </button>
      </div>
    </div>
  );
}

'use client';

import { useRouter } from 'next/navigation';
import { useEffect } from 'react';

export default function VoiceExecutor() {
  const router = useRouter();

  useEffect(() => {
    const tick = setInterval(async () => {
      try {
        const res = await fetch('/api/voice');
        const cmd = await res.json();
        if (!cmd?.action) return;

        if (cmd.action === 'navigate') {
          router.push(cmd.path);
        }

        if (cmd.action === 'addImage') {
          const img = document.createElement('img');
          img.src = cmd.src;
          img.style.maxWidth = '400px';
          img.style.position = 'fixed';
          img.style.bottom = '20px';
          img.style.left = '20px';
          img.style.zIndex = '9999';
          document.body.appendChild(img);
        }

        if (cmd.action === 'injectHTML') {
          const wrapper = document.createElement('div');
          wrapper.innerHTML = cmd.html;
          document.body.appendChild(wrapper);
        }

        if (cmd.action === 'setCSS') {
          const el = document.querySelector(cmd.selector) as HTMLElement;
          if (!el) return;
          Object.entries(cmd.css as Record<string, string>).forEach(([k, v]) => {
            el.style.setProperty(k, v);
          });
        }

        if (cmd.action === 'deploy') {
          await fetch('/api/git', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ message: 'voice deploy' }),
          });
          alert('Deploying to production...');
        }
      } catch (e: unknown) {
        // Silent fail
      }
    }, 1000);

    return () => clearInterval(tick);
  }, [router]);

  return null;
}

'use client';

import { useEffect } from 'react';

import { handleVoicePayload } from '@/lib/voice/payloadHandler';

export default function VoiceListener() {
  useEffect(() => {
    const interval = setInterval(async () => {
      try {
        const res = await fetch('/api/voice');
        const cmd = await res.json();
        if (!cmd || !cmd.action) return;

        // Phase 63 & 32: Route to Payload Handler
        // Phase 34: Monetization Engine
        if (cmd.target === 'ui' || cmd.target === 'style' || cmd.target === 'monetization') {
            handleVoicePayload(cmd);
            return; 
        }

        // Phase 63: Avatar Voice Control
        if (cmd.target === 'avatar') {
            window.dispatchEvent(new CustomEvent('voice-command', { detail: cmd }));
            return;
        }

        if (cmd.action === 'addText') {
          const el = document.createElement('div');
          el.innerText = cmd.text;
          el.style.position = 'fixed';
          el.style.bottom = '20px';
          el.style.right = '20px';
          el.style.color = 'gold';
          el.style.zIndex = '9999';
          document.body.appendChild(el);
        }

        if (cmd.action === 'addVideo') {
          const video = document.createElement('video');
          video.src = cmd.src;
          video.autoplay = true;
          video.loop = true;
          video.muted = true;
          video.style.position = 'fixed';
          video.style.inset = '0';
          video.style.zIndex = '-1';
          document.body.appendChild(video);
        }

        if (cmd.action === 'changeTheme') {
            // Legacy support, but payloadHandler handles this better now
             handleVoicePayload({ target: 'style', path: 'accent', value: cmd.color });
        }
      } catch (e: unknown) {
        // Silent fail on API errors
      }
    }, 1200);

    return () => clearInterval(interval);
  }, []);

  return null;
}

