  artist: string;
  src: string;
}

const songs: Song[] = [
  {
    title: "Symphony",
    artist: "Clean Bandit ft. Zara Larsson",
    src: "https://github.com/ecemgo/mini-samples-great-tricks/raw/main/song-list/Clean-Bandit-Symphony.mp3",
