"use client";
import { useEffect, useState } from "react";

interface VoiceListenerProps {
  isListening: boolean;
  onToggle: () => void;
  onCommand: (transcript: string) => void;
}

export default function VoiceListener({
  isListening,
  onToggle,
  onCommand,
}: VoiceListenerProps) {
  const [transcript, setTranscript] = useState("");
  const [isSupported, setIsSupported] = useState(true);

  useEffect(() => {
    // Check if browser supports Web Speech API
    if (
      !("webkitSpeechRecognition" in window) &&
      !("SpeechRecognition" in window)
    ) {
      setIsSupported(false);
      return;
    }

    const SpeechRecognition =
      (window as any).SpeechRecognition ||
      (window as any).webkitSpeechRecognition;
    const recognition = new SpeechRecognition();

    recognition.continuous = false;
    recognition.interimResults = true;
    recognition.lang = "en-US";

    recognition.onresult = (event: any) => {
      const current = event.resultIndex;
      const transcriptText = event.results[current][0].transcript;
      setTranscript(transcriptText);

      // If final result, execute command
      if (event.results[current].isFinal) {
        onCommand(transcriptText);
        setTranscript("");
        onToggle(); // Stop listening after command
      }
    };

    recognition.onerror = (event: any) => {
      console.error("Speech recognition error:", event.error);
      if (event.error === "not-allowed") {
        setIsSupported(false);
      }
    };

    if (isListening) {
      recognition.start();
    } else {
      recognition.stop();
    }

    return () => {
      recognition.stop();
    };
  }, [isListening, onCommand]);

  if (!isSupported) {
    return (
      <div className="text-center py-8 text-gray-400">
        <p className="text-4xl mb-2">üé§</p>
        <p className="text-sm">Voice commands not supported in this browser</p>
        <p className="text-xs mt-1">Try Chrome, Edge, or Safari</p>
      </div>
    );
  }

  return (
    <div className="text-center space-y-4">
      <button
        onClick={onToggle}
        className={`w-32 h-32 rounded-full flex items-center justify-center text-6xl transition-all ${
          isListening
            ? "bg-red-500 hover:bg-red-600 animate-pulse shadow-lg shadow-red-500/50"
            : "bg-yellow-600 hover:bg-yellow-700 shadow-lg shadow-yellow-500/50"
        }`}
      >
        {isListening ? "üî¥" : "üé§"}
      </button>

      <div className="min-h-[60px]">
        {isListening ? (
          <div>
            <p className="text-sm text-gray-400 mb-2">Listening...</p>
            {transcript && (
              <p className="text-lg text-white font-medium px-4 py-2 bg-black/50 rounded-lg inline-block">
                "{transcript}"
              </p>
            )}
          </div>
        ) : (
          <p className="text-sm text-gray-500">Click microphone to start</p>
        )}
      </div>

      <div className="text-xs text-gray-500 space-y-1">
        <p>Try saying:</p>
        <p className="text-yellow-400">"Deploy my site"</p>
        <p className="text-yellow-400">"Update the hero section"</p>
        <p className="text-yellow-400">"Generate new content"</p>
      </div>
    </div>
  );
}

'use client';

import {
  Billboard,
  Capsule,
  ContactShadows,
  Cylinder,
  Environment,
  Float,
  MeshDistortMaterial,
  RoundedBox,
  Sparkles,
  Sphere,
  Text,
} from '@react-three/drei';
import { Canvas, useFrame, useThree } from '@react-three/fiber';
import { useEffect, useMemo, useRef, useState } from 'react';
import * as THREE from 'three';

// ============================================
// PREMIUM 3D FEMALE AVATAR COMPONENT
// Works on both public and admin sections
// ============================================

interface AvatarProps {
  expression?: string;
  isSpeaking?: boolean;
  variant?: 'full' | 'bust' | 'head';
  showHUD?: boolean;
  interactionEnabled?: boolean;
}

// Hair strand component for realistic hair
function HairStrands({ color = '#1a1a2e' }: { color?: string }) {
  const strandsRef = useRef<THREE.Group>(null);

  useFrame((state) => {
    if (strandsRef.current) {
      // Subtle hair physics
      strandsRef.current.rotation.z = Math.sin(state.clock.elapsedTime * 2) * 0.02;
    }
  });

  return (
    <group ref={strandsRef}>
      {/* Main hair volume */}
      <Sphere args={[0.38, 32, 32]} position={[0, 0.05, -0.05]}>
        <meshStandardMaterial color={color} roughness={0.8} metalness={0.1} />
      </Sphere>
      {/* Side hair */}
      <Capsule args={[0.08, 0.5, 8, 16]} position={[-0.28, -0.2, 0]} rotation={[0, 0, 0.3]}>
        <meshStandardMaterial color={color} roughness={0.7} />
      </Capsule>
      <Capsule args={[0.08, 0.5, 8, 16]} position={[0.28, -0.2, 0]} rotation={[0, 0, -0.3]}>
        <meshStandardMaterial color={color} roughness={0.7} />
      </Capsule>
      {/* Back hair (long) */}
      <Capsule args={[0.15, 1.2, 8, 16]} position={[0, -0.6, -0.15]} rotation={[0.1, 0, 0]}>
        <meshStandardMaterial color={color} roughness={0.7} />
      </Capsule>
    </group>
  );
}

// Eye component with blinking and expression
function Eye({
  position,
  expression,
  isSpeaking,
}: {
  position: [number, number, number];
  expression: string;
  isSpeaking: boolean;
}) {
  const eyeRef = useRef<THREE.Mesh>(null);
  const pupilRef = useRef<THREE.Mesh>(null);
  const [blink, setBlink] = useState(false);

  // Natural blinking
  useEffect(() => {
    const blinkInterval = setInterval(
      () => {
        setBlink(true);
        setTimeout(() => setBlink(false), 150);
      },
      3000 + Math.random() * 2000
    );
    return () => clearInterval(blinkInterval);
  }, []);

  useFrame((state) => {
    if (pupilRef.current) {
      // Subtle eye movement following virtual point
      const t = state.clock.elapsedTime;
      pupilRef.current.position.x = Math.sin(t * 0.5) * 0.01;
      pupilRef.current.position.y = Math.cos(t * 0.7) * 0.005;
    }
  });

  const getIrisColor = () => {
    switch (expression) {
      case 'happy':
        return '#00e5ff';
      case 'excited':
        return '#ffeb3b';
      case 'angry':
        return '#ff1744';
      case 'sad':
        return '#5c6bc0';
      case 'talking':
        return '#00bcd4';
      default:
        return '#00aaff';
    }
  };

  return (
    <group position={position}>
      {/* Eyeball */}
      <Sphere ref={eyeRef} args={[0.045, 32, 32]} scale={[1, blink ? 0.1 : 1, 1]}>
        <meshStandardMaterial color="#ffffff" roughness={0.1} metalness={0} />
      </Sphere>
      {/* Iris */}
      <Sphere args={[0.025, 32, 32]} position={[0, 0, 0.025]}>
        <meshStandardMaterial
          color={getIrisColor()}
          emissive={getIrisColor()}
          emissiveIntensity={isSpeaking ? 1.5 : 0.5}
        />
      </Sphere>
      {/* Pupil */}
      <Sphere ref={pupilRef} args={[0.012, 16, 16]} position={[0, 0, 0.035]}>
        <meshStandardMaterial color="#000000" />
      </Sphere>
    </group>
  );
}

// Lips with speaking animation
function Lips({ isSpeaking, expression }: { isSpeaking: boolean; expression: string }) {
  const lipsRef = useRef<THREE.Mesh>(null);

  useFrame((state) => {
    if (lipsRef.current && isSpeaking) {
      // Natural speaking motion
      const t = state.clock.elapsedTime;
      lipsRef.current.scale.y = 0.8 + Math.sin(t * 15) * 0.3;
      lipsRef.current.scale.x = 1 + Math.cos(t * 12) * 0.1;
    } else if (lipsRef.current) {
      // Idle expression
      lipsRef.current.scale.y = expression === 'happy' ? 1.1 : 1;
      lipsRef.current.scale.x = expression === 'happy' ? 1.2 : 1;
    }
  });

  return (
    <group position={[0, -0.12, 0.28]}>
      {/* Upper lip */}
      <RoundedBox ref={lipsRef} args={[0.08, 0.015, 0.02]} radius={0.005}>
        <meshStandardMaterial color="#d4848a" roughness={0.3} />
      </RoundedBox>
      {/* Lower lip */}
      <RoundedBox args={[0.07, 0.02, 0.02]} position={[0, -0.015, 0]} radius={0.005}>
        <meshStandardMaterial color="#c77b81" roughness={0.3} />
      </RoundedBox>
    </group>
  );
}

// Main Avatar Body
function FemaleAvatarBody({
  expression = 'neutral',
  isSpeaking = false,
  variant = 'full',
}: {
  expression: string;
  isSpeaking: boolean;
  variant: 'full' | 'bust' | 'head';
}) {
  const groupRef = useRef<THREE.Group>(null);
  const { mouse } = useThree();

  // Breathing and mouse tracking
  useFrame((state) => {
    if (groupRef.current) {
      const t = state.clock.elapsedTime;

      // Breathing animation
      groupRef.current.position.y = Math.sin(t * 0.8) * 0.03;

      // Mouse parallax (subtle head turning)
      const targetRotY = mouse.x * 0.3;
      const targetRotX = -mouse.y * 0.15;
      groupRef.current.rotation.y = THREE.MathUtils.lerp(
        groupRef.current.rotation.y,
        targetRotY,
        0.05
      );
      groupRef.current.rotation.x = THREE.MathUtils.lerp(
        groupRef.current.rotation.x,
        targetRotX,
        0.05
      );
    }
  });

  const skinColor = '#f5dcd4';
  const bodyColor = '#1a1a2e';
  const accentColor = '#D4AF37'; // Gold accent

  return (
    <group ref={groupRef}>
      {/* --- HEAD --- */}
      <group position={[0, variant === 'head' ? 0 : 1.6, 0]}>
        {/* Face */}
        <Sphere args={[0.32, 64, 64]}>
          <MeshDistortMaterial
