'use client';

import { useState } from 'react';

// Voice-to-Code Hook
export function useVoiceToCode() {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const generateCode = async (
    prompt: string,
    action: "preview" | "apply" | "deploy"
  ) => {
    setLoading(true);
    setError(null);

    try {
      const response = await fetch('/api/voice-to-code', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ prompt, action }),
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || 'Failed to generate code');
      }

      return data;
    } catch (err: unknown) {
      const errorMessage = err instanceof Error ? (err instanceof Error ? (err instanceof Error ? err.message : "Unknown error") : "Unknown error") : 'An error occurred';
      setError(errorMessage);
      throw err;
    } finally {
      setLoading(false);
    }
  };

  const transcribeAndGenerate = async (
    audioBase64: string,
    action: "preview" | "apply" | "deploy"
  ) => {
    setLoading(true);
    setError(null);

    try {
      const response = await fetch('/api/voice-to-code', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ audio: audioBase64, action }),
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || 'Failed to process audio');
      }

      return data;
    } catch (err: unknown) {
      const errorMessage = err instanceof Error ? (err instanceof Error ? (err instanceof Error ? err.message : "Unknown error") : "Unknown error") : 'An error occurred';
      setError(errorMessage);
      throw err;
    } finally {
      setLoading(false);
    }
  };

  return { generateCode, transcribeAndGenerate, loading, error };
}

// PayPal Checkout Hook
export function usePayPalCheckout() {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const createOrder = async (items: any[], userId?: string) => {
    setLoading(true);
    setError(null);

    try {
      const response = await fetch('/api/paypal/create-order', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
