export async function getDashboardStats() {
  try {
    const database = await connectToDatabase();

    const now = new Date();
    const lastMonth = new Date();
    lastMonth.setMonth(now.getMonth() - 1);

    // Get current month stats
    const currentStats = await database
      .collection('analytics')
      .findOne({ timestamp: { $gte: lastMonth } }, { sort: { timestamp: -1 } });

    // Get order stats
    const ordersCollection = database.collection('orders');
    const totalOrders = await ordersCollection.countDocuments({
      createdAt: { $gte: lastMonth },
      status: 'completed',
    });

    const revenueAgg = await ordersCollection
      .aggregate([
        {
          $match: {
            createdAt: { $gte: lastMonth },
            status: 'completed',
          },
        },
        {
          $group: {
            _id: null,
            total: { $sum: '$total' },
          },
        },
      ])
      .toArray();

    const revenue = revenueAgg[0]?.total || 0;

    return {
      totalRevenue: revenue,
      activeUsers: currentStats?.activeUsers || 0,
      storeOrders: totalOrders,
      liveViewers: currentStats?.liveViewers || 0,
      pageViews: currentStats?.pageViews || 0,
      conversionRate: currentStats?.conversionRate || 0,
    };
  } catch (error: unknown) {
    console.error('Get dashboard stats error:', error);
    throw new Error('Failed to fetch dashboard stats');
  }
}

"use client";

export function playSound(type: 'hover' | 'click' | 'success' | 'nav') {
  if (typeof window === 'undefined') return;
  
  try {
    const audioContext = new (window.AudioContext || (window as any).webkitAudioContext)();
    const oscillator = audioContext.createOscillator();
    const gainNode = audioContext.createGain();
    
    oscillator.connect(gainNode);
    gainNode.connect(audioContext.destination);
    
    switch (type) {
      case 'hover':
        // Soft ting/sparkle
        oscillator.frequency.setValueAtTime(1200, audioContext.currentTime);
        oscillator.frequency.exponentialRampToValueAtTime(600, audioContext.currentTime + 0.1);
        gainNode.gain.setValueAtTime(0.15, audioContext.currentTime);
        gainNode.gain.exponentialRampToValueAtTime(0.01, audioContext.currentTime + 0.1);
        oscillator.start(audioContext.currentTime);
        oscillator.stop(audioContext.currentTime + 0.1);
        break;
        
      case 'click':
        // Cash register/coin
        oscillator.frequency.setValueAtTime(800, audioContext.currentTime);
        oscillator.frequency.exponentialRampToValueAtTime(400, audioContext.currentTime + 0.15);
        gainNode.gain.setValueAtTime(0.2, audioContext.currentTime);
        gainNode.gain.exponentialRampToValueAtTime(0.01, audioContext.currentTime + 0.15);
        oscillator.start(audioContext.currentTime);
        oscillator.stop(audioContext.currentTime + 0.15);
        break;
        
      case 'success':
        // Chime
        oscillator.frequency.setValueAtTime(1500, audioContext.currentTime);
        oscillator.frequency.exponentialRampToValueAtTime(800, audioContext.currentTime + 0.2);
        gainNode.gain.setValueAtTime(0.18, audioContext.currentTime);
        gainNode.gain.exponentialRampToValueAtTime(0.01, audioContext.currentTime + 0.2);
        oscillator.start(audioContext.currentTime);
        oscillator.stop(audioContext.currentTime + 0.2);
        break;
        
      case 'nav':
        // Whoosh transition
        oscillator.frequency.setValueAtTime(300, audioContext.currentTime);
        oscillator.frequency.exponentialRampToValueAtTime(150, audioContext.currentTime + 0.25);
        gainNode.gain.setValueAtTime(0.12, audioContext.currentTime);
        gainNode.gain.exponentialRampToValueAtTime(0.01, audioContext.currentTime + 0.25);
        oscillator.start(audioContext.currentTime);
        oscillator.stop(audioContext.currentTime + 0.25);
        break;
    }
  } catch (error: unknown) {
    // Silently fail if audio context not supported
    console.debug('Audio not supported:', error);
  }
}

