"use client";

import { createContext, useContext, ReactNode, useState, useCallback, useEffect } from "react";
import { useRouter } from "next/navigation";

interface VoiceCommand {
  transcript: string;
  confidence: number;
  timestamp: Date;
}

interface VoiceBridgeContextType {
  isListening: boolean;
  lastCommand: VoiceCommand | null;
  startListening: () => void;
  stopListening: () => void;
  executeCommand: (command: string) => Promise<void>;
}

const VoiceBridgeContext = createContext<VoiceBridgeContextType | undefined>(undefined);

export function VoiceBridgeProvider({ children }: { children: ReactNode }) {
  const [isListening, setIsListening] = useState(false);
  const [lastCommand, setLastCommand] = useState<VoiceCommand | null>(null);
  const router = useRouter();

  const executeCommand = useCallback(async (command: string) => {
    const lower = command.toLowerCase();
    
    // Navigation commands
    if (lower.includes("go to") || lower.includes("navigate to") || lower.includes("open")) {
      if (lower.includes("home")) router.push("/");
      else if (lower.includes("about")) router.push("/about");
      else if (lower.includes("blog")) router.push("/blog");
      else if (lower.includes("contact")) router.push("/contact");
      else if (lower.includes("store")) router.push("/store");
      else if (lower.includes("live")) router.push("/live");
      else if (lower.includes("admin")) router.push("/admin");
    }
    
    // UI commands
    else if (lower.includes("scroll down")) {
      window.scrollBy({ top: window.innerHeight, behavior: "smooth" });
    }
    else if (lower.includes("scroll up")) {
      window.scrollBy({ top: -window.innerHeight, behavior: "smooth" });
    }
    else if (lower.includes("scroll to top")) {
      window.scrollTo({ top: 0, behavior: "smooth" });
    }
    
    setLastCommand({
      transcript: command,
      confidence: 0.9,
      timestamp: new Date()
    });
  }, [router]);

  const startListening = useCallback(() => {
    if (!('webkitSpeechRecognition' in window) && !('SpeechRecognition' in window)) {
      console.warn("Speech recognition not supported");
      return;
    }

    const SpeechRecognition = (window as any).SpeechRecognition || (window as any).webkitSpeechRecognition;
    const recognition = new SpeechRecognition();
    
    recognition.continuous = true;
    recognition.interimResults = false;
    recognition.lang = 'en-US';

    recognition.onresult = (event: any) => {
      const transcript = event.results[event.results.length - 1][0].transcript;
      executeCommand(transcript);
    };

    recognition.start();
    setIsListening(true);
  }, [executeCommand]);

  const stopListening = useCallback(() => {
    setIsListening(false);
  }, []);

  return (
    <VoiceBridgeContext.Provider value={{
      isListening,
      lastCommand,
      startListening,
      stopListening,
      executeCommand
    }}>
      {children}
    </VoiceBridgeContext.Provider>
  );
}

export function useVoiceBridge() {
  const context = useContext(VoiceBridgeContext);
  if (!context) throw new Error("useVoiceBridge must be used within VoiceBridgeProvider");
  return context;
}

// Auto-fix script for common ESLint issues
const fs = require('fs');
const path = require('path');

const fixes = [
  // Fix catch (error: any) -> catch (error: unknown)
  {
    pattern: /catch\s*\(\s*(\w+)\s*:\s*any\s*\)/g,
    replacement: 'catch ($1: unknown)',
    description: 'Fix catch error any types',
  },
  // Fix Record<string, any> -> Record<string, unknown>
  {
    pattern: /Record<string,\s*any>/g,
    replacement: 'Record<string, unknown>',
    description: 'Fix Record any types',
  },
  // Fix function params (param: any) -> (param: unknown)
  {
    pattern: /\(\s*(\w+)\s*:\s*any\s*\)/g,
    replacement: '($1: unknown)',
    description: 'Fix function parameter any types',
  },
  // Fix unused error variables in catch blocks
  {
    pattern: /catch\s*\(\s*error\s*\)\s*{/g,
    replacement: 'catch (_error) {',
    description: 'Prefix unused error variables with underscore',
  },
  {
    pattern: /catch\s*\(\s*e\s*\)\s*{/g,
    replacement: 'catch (_e) {',
    description: 'Prefix unused e variables with underscore',
  },
  // Fix unescaped quotes in JSX
  {
    pattern: />\s*"([^"<>]+)"\s*</g,
    replacement: '>&quot;$1&quot;<',
    description: 'Escape double quotes in JSX',
  },
  {
    pattern: />\s*'([^'<>]+)'\s*</g,
    replacement: '>&apos;$1&apos;<',
    description: 'Escape single quotes in JSX',
  },
];

function processFile(filePath) {
  try {
    let content = fs.readFileSync(filePath, 'utf8');
    let modified = false;

    fixes.forEach((fix) => {
      if (fix.pattern.test(content)) {
        content = content.replace(fix.pattern, fix.replacement);
        modified = true;
      }
    });

    if (modified) {
      fs.writeFileSync(filePath, content, 'utf8');
      console.log(`‚úì Fixed: ${filePath}`);
      return true;
    }
    return false;
  } catch (err) {
    console.error(`‚úó Error processing ${filePath}:`, err.message);
    return false;
  }
}

function walkDir(dir, filePattern = /\.(ts|tsx)$/) {
  const files = fs.readdirSync(dir);
  let fixedCount = 0;

  files.forEach((file) => {
    const filePath = path.join(dir, file);
    const stat = fs.statSync(filePath);

    if (stat.isDirectory()) {
      // Skip node_modules and .next
      if (file !== 'node_modules' && file !== '.next' && file !== '.git') {
        fixedCount += walkDir(filePath, filePattern);
      }
    } else if (filePattern.test(file)) {
      if (processFile(filePath)) {
        fixedCount++;
      }
    }
  });

  return fixedCount;
}

// Run the fixer
console.log('üîß Starting auto-fix...\n');
const rootDir = process.cwd();
const fixedFiles = walkDir(rootDir);
console.log(`\n‚úÖ Fixed ${fixedFiles} files`);

